<script type="text/javascript">
$(document).ready(function(){
var current = 1,current_step,next_step,steps;
steps = $("fieldset").length;
$("#next").click(function(){
if(myFunction1()){
current_step = $(this).parent();
next_step = $(this).parent().next();
next_step.show();
current_step.hide();
setProgressBar(++current); } });
$("#next2").click(function(){
if(qualificationFunction()){
current_step = $(this).parent();
next_step = $(this).parent().next();
next_step.show();
current_step.hide();
setProgressBar(++current); } });
$(".previous").click(function(){
current_step = $(this).parent();
next_step = $(this).parent().prev();
next_step.show();
current_step.hide();
setProgressBar(--current); });
setProgressBar(current);
function setProgressBar(curStep){
var percent = parseFloat(100 / steps) * curStep;
percent = percent.toFixed();
$(".progress-bar")
.css("width",percent+"%")
.html(percent+"%"); } });
$(function(){
$(".static_class").click(function(){
if($(this).val() === "Yes")
$("#extra").show(0);
else 
$("#extra").hide(0);
});
});
function myFunction1() {
var x = document.getElementById("name1").value;
if (x=='') {alert("First name must be filled out"); return false; }
var x = document.getElementById("name2").value; if (x=='') {alert("Last name must be filled out"); return false; }
var x = document.getElementById("p_add").value; if (x=='') {alert("Address must be filled out");   return false; }
var x = document.getElementById("city").value;  if (x=='') {alert("City must be filled out");      return false; }
var x = document.getElementById("state").value; if (x=='') {alert("State must be filled out");     return false; }
var x = document.getElementById("zip_code").value;if(x=='') {alert("ZIP Code must be filled out"); return false; }
var x = document.getElementById("email").value;
var atpos = x.indexOf("@");
var dotpos = x.lastIndexOf(".");
if (atpos<1 || dotpos<atpos+2 || dotpos+2>=x.length) {
alert("Not a valid e-mail address"); return false; }

var phone = document.getElementById("phone").value;
var regExp = /^(\([0-9]{3}\) |[0-9]{3}-)[0-9]{3}-[0-9]{4}/;
var phone = phone.match(regExp);
if (phone) {
  return true;
  }
  alert('Invalid Phone number format. Please enter in this format (xxx-xxx-xxxx).');
  return false;
}


$('#phone').keyup(function() {
        var val = this.value.replace(/\D/g, '');
        var newVal = '';
        while (val.length > 4) {
          newVal += val.substr(0, 3) + '-';
          val = val.substr(3);
        }
        newVal += val;
        this.value = newVal;
    });


function qualificationFunction() {
var x = document.getElementById("qualification").value;
if (x=='') { alert("Qualification must be filled out"); return false; } 
return true; }
document.getElementById("uploadBtn").onchange = function () {
    document.getElementById("uploadFile").value = this.value;
};
function show() { document.getElementById('area').style.display = 'inline-block'; }
function hide() { document.getElementById('area').style.display = 'none'; }
</script>
<script type="text/javascript">
    function captchaCode() {
  var Numb1, Numb2, Numb3, Numb4, Code;     
  Numb1 = (Math.ceil(Math.random() * 10)-1).toString();
  Numb2 = (Math.ceil(Math.random() * 10)-1).toString();
  Numb3 = (Math.ceil(Math.random() * 10)-1).toString();
  Numb4 = (Math.ceil(Math.random() * 10)-1).toString();
  
  Code = Numb1 + Numb2 + Numb3 + Numb4;
  $("#captcha span").remove();
  $("#captcha input").remove();
  $("#captcha").append("<span id='code'>" + Code + "</span><input type='button' onclick='captchaCode();'>");
}
$(function() {
captchaCode();
$('#regiration_form').submit(function(){
var captchaVal = $("#code").text();
var captchaCode = $(".captcha").val();
if (captchaVal == captchaCode) {
return true;
}
else {
alert('Please Enter the correct captcha value ');
return false;
}
return true;
});       
});
</script>